mapper.insert:
insert into advanced_collective (id, name, honorary_title,
owned_city_industry, principal_name, contact_detail,
outstanding_deed, is_current, addition_time
)
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{honoraryTitle,jdbcType=VARCHAR},
#{ownedCityIndustry,jdbcType=VARCHAR}, #{principalName,jdbcType=VARCHAR}, #{contactDetail,jdbcType=VARCHAR},
#{outstandingDeed,jdbcType=VARCHAR}, #{isCurrent,jdbcType=TINYINT}, #{additionTime,jdbcType=DATE}
)

mapper.delete:
delete from advanced_collective
where id in

  #{id,jdbcType=VARCHAR}

and is_current = #{isCurrent,jdbcType=TINYINT}

mapper.select:
select

from advanced_collective
where 1=1

     and id = #{ac.id,jdbcType=VARCHAR}


    and name = #{ac.name,jdbcType=VARCHAR}


    and honorary_title = #{ac.honoraryTitle,jdbcType=VARCHAR}


    and owned_city_industry = #{ac.ownedCityIndustry,jdbcType=VARCHAR}


    and principal_name = #{ac.principalName,jdbcType=VARCHAR}


    and contact_detail = #{ac.contactDetail,jdbcType=VARCHAR}


    and outstanding_deed = #{ac.outstandingDeed,jdbcType=VARCHAR}


    and is_current = #{ac.isCurrent,jdbcType=TINYINT}


    and addition_time = #{ac.additionTime,jdbcType=DATE}

limit #{start},#{rows}

select

from advanced_collective
where addition_time BETWEEN #{beginDate,jdbcType=DATE}
       AND #{endDate,jdbcType=DATE}

select

from advanced_collective
where id in

  #{id,jdbcType=VARCHAR}

and is_current = #{isCurrent,jdbcType=TINYINT}

select
count(*)
from advanced_collective
where 1=1

  and id = #{id,jdbcType=VARCHAR}


  and name = #{name,jdbcType=VARCHAR}


  and honorary_title = #{honoraryTitle,jdbcType=VARCHAR}


  and owned_city_industry = #{ownedCityIndustry,jdbcType=VARCHAR}


  and principal_name = #{principalName,jdbcType=VARCHAR}


  and contact_detail = #{contactDetail,jdbcType=VARCHAR}


  and outstanding_deed = #{outstandingDeed,jdbcType=VARCHAR}


  and is_current = #{isCurrent,jdbcType=TINYINT}


  and addition_time = #{additionTime,jdbcType=DATE}

select honorary_title as title,
count(*) as num
from advanced_collective

  where addition_time BETWEEN #{beginDate,jdbcType=DATE}
  AND #{endDate,jdbcType=DATE}

group by honorary_title
