mapper.select:
select
from advanced_person where addition_time BETWEEN
#{beginDate,jdbcType=DATE} AND #{endDate,jdbcType=DATE}

select
from advanced_person where id in
#{id,jdbcType=VARCHAR}

select

from advanced_person ap
where 1=1

  and id =#{pa.id,jdbcType=VARCHAR}


  and honorary_title =#{pa.honoraryTitle,jdbcType=VARCHAR}


and treatment = #{pa.treatment,jdbcType=INTEGER}


and name =  #{pa.name,jdbcType=VARCHAR}


and gender =  #{pa.gender,jdbcType=TINYINT}


and nation =  #{pa.nation,jdbcType=VARCHAR}


 and birthday = #{pa.birthday,jdbcType=DATE}


and cultural_level =  #{pa.culturalLevel,jdbcType=VARCHAR}


and political_status =  #{pa.politicalStatus,jdbcType=VARCHAR}


 and owned_city_industry = #{pa.ownedCityIndustry,jdbcType=VARCHAR}


 and organization = #{pa.organization,jdbcType=VARCHAR}


 and duty = #{pa.duty,jdbcType=VARCHAR}


 and identity_card = #{pa.identityCard,jdbcType=VARCHAR}


and tel = #{pa.tel,jdbcType=VARCHAR}


and status =  #{pa.status,jdbcType=INTEGER}


and  status_information = #{pa.statusInformation,jdbcType=VARCHAR}


 and is_current = #{pa.isCurrent,jdbcType=TINYINT}


 and addition_time = #{pa.additionTime,jdbcType=DATE}


and  physical_condition = #{pa.physicalCondition,jdbcType=VARCHAR}


 and family_difficulties_and_employment =  #{pa.familyDifficultiesAndEmployment,jdbcType=VARCHAR}


and outstanding_deed = #{pa.outstandingDeed,jdbcType=VARCHAR}





limit #{skip},#{rows}

select
count(*)
  from advanced_person
